//! # Testing with Generated ANSI Sequences
//!
//! This test module uses **generated byte sequences** from the input sequence
//! generator to ensure comprehensive testing of the system's real-world usage pattern:
//!
//! 1. **Real-world integration**: Tests that our generator produces valid sequences our
//!    parser can consume (production usage pattern)
//! 2. **Scalability**: Easy to test many input combinations without hardcoding
//! 3. **Consistency**: Uses the same generator as all other PTY tests
//!
//! Protocol conformance is validated separately in [`validation_tests`] using hardcoded
//! sequences to ensure compliance with the VT-100 spec.
//!
//! See the [parent module documentation] for full testing philosophy.
//!
//! [`validation_tests`]: mod@crate::core::ansi::vt_100_terminal_input_parser::validation_tests
//! [parent module documentation]: mod@super#testing-philosophy

use crate::{ControlledChild, InputEvent, PtyPair, PtyTestMode, TermPos,
            core::ansi::{generator::generate_keyboard_sequence,
                         vt_100_terminal_input_parser::ir_event_types::{VT100InputEventIR,
                                                                        VT100KeyModifiersIR,
                                                                        VT100MouseActionIR,
                                                                        VT100MouseButtonIR}},
            generate_pty_test,
            tui::terminal_lib_backends::direct_to_ansi::DirectToAnsiInputDevice};
use std::{io::{BufRead, BufReader, Write},
          time::Duration};

/// Ready signal sent by controlled process after initialization.
const CONTROLLED_READY: &str = "CONTROLLED_READY";

// XMARK: Process isolated test with PTY.

generate_pty_test! {
    test_fn: test_pty_mouse_events,
    controller: pty_controller_entry_point,
    controlled: pty_controlled_entry_point,
    mode: PtyTestMode::Raw,
}

/// PTY Controller: Send mouse event sequences and verify parsing
#[allow(clippy::too_many_lines)]
fn pty_controller_entry_point(pty_pair: PtyPair, mut child: ControlledChild) {
    eprintln!("ğŸš€ PTY Controller: Starting mouse events test...");

    let mut writer = pty_pair.controller().take_writer().expect("Failed to get writer");
    let reader = pty_pair
        .controller()
        .try_clone_reader()
        .expect("Failed to get reader");
    let mut buf_reader = BufReader::new(reader);

    eprintln!("ğŸ“ PTY Controller: Waiting for controlled process to start...");

    // Wait for controlled to confirm it's running. The controlled process sends
    // TEST_RUNNING and CONTROLLED_READY immediately on startup.
    let mut test_running_seen = false;

    loop {
        let mut line = String::new();
        match buf_reader.read_line(&mut line) {
            Ok(0) => panic!("EOF reached before controlled started"),
            Ok(_) => {
                let trimmed = line.trim();
                eprintln!("  â† Controlled output: {trimmed}");

                if trimmed.contains("TEST_RUNNING") {
                    test_running_seen = true;
                    eprintln!("  âœ“ Test is running in controlled");
                }
                if trimmed.contains(CONTROLLED_READY) {
                    eprintln!("  âœ“ Controlled process confirmed running!");
                    break;
                }
            }
            Err(e) => panic!("Read error while waiting for controlled: {e}"),
        }
    }

    assert!(
        test_running_seen,
        "Controlled test never started running (no TEST_RUNNING output)"
    );

    // Generated mouse events using the input sequence generator
    let mouse_events: Vec<(&str, VT100InputEventIR)> = vec![
        ("Left Click", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Left,
            pos: TermPos::from_one_based(10, 5),
            action: VT100MouseActionIR::Press,
            modifiers: VT100KeyModifiersIR::default(),
        }),
        ("Left Release", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Left,
            pos: TermPos::from_one_based(10, 5),
            action: VT100MouseActionIR::Release,
            modifiers: VT100KeyModifiersIR::default(),
        }),
        ("Right Click", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Right,
            pos: TermPos::from_one_based(20, 10),
            action: VT100MouseActionIR::Press,
            modifiers: VT100KeyModifiersIR::default(),
        }),
        ("Middle Click", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Middle,
            pos: TermPos::from_one_based(30, 15),
            action: VT100MouseActionIR::Press,
            modifiers: VT100KeyModifiersIR::default(),
        }),
        ("Scroll Up", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Left,
            pos: TermPos::from_one_based(25, 12),
            action: VT100MouseActionIR::Scroll(crate::core::ansi::vt_100_terminal_input_parser::VT100ScrollDirectionIR::Up),
            modifiers: VT100KeyModifiersIR::default(),
        }),
        ("Scroll Down", VT100InputEventIR::Mouse {
            button: VT100MouseButtonIR::Left,
            pos: TermPos::from_one_based(25, 12),
            action: VT100MouseActionIR::Scroll(crate::core::ansi::vt_100_terminal_input_parser::VT100ScrollDirectionIR::Down),
            modifiers: VT100KeyModifiersIR::default(),
        }),
    ];

    eprintln!(
        "ğŸ“ PTY Controller: Sending {} mouse event sequences...",
        mouse_events.len()
    );

    for (desc, event) in &mouse_events {
        let sequence = generate_keyboard_sequence(event)
            .unwrap_or_else(|| panic!("Failed to generate sequence for: {desc}"));
        eprintln!("  â†’ Sending: {desc} ({sequence:?})");

        writer
            .write_all(&sequence)
            .expect("Failed to write sequence");
        writer.flush().expect("Failed to flush");

        std::thread::sleep(Duration::from_millis(100));

        // Read responses until we get a mouse event line or skip
        let mut found_response = false;
        for _ in 0..5 {
            let mut line = String::new();
            match buf_reader.read_line(&mut line) {
                Ok(0) => {
                    // EOF is okay during mouse testing
                    break;
                }
                Ok(_) => {
                    let trimmed = line.trim();

                    // Check if it's a mouse event line or any output
                    if trimmed.starts_with("Mouse:") || !trimmed.is_empty() {
                        eprintln!("  âœ“ {desc}: {trimmed}");
                        found_response = true;
                        break;
                    }
                }
                Err(e) if e.kind() == std::io::ErrorKind::WouldBlock => {
                    std::thread::sleep(Duration::from_millis(10));
                }
                Err(_) => {
                    break;
                }
            }
        }

        if !found_response {
            eprintln!("  âš ï¸  No response for {desc}");
        }
    }

    eprintln!("ğŸ§¹ PTY Controller: Cleaning up...");

    drop(writer);

    match child.wait() {
        Ok(status) => {
            eprintln!("âœ… PTY Controller: Controlled process exited: {status:?}");
        }
        Err(e) => {
            panic!("Failed to wait for controlled process: {e}");
        }
    }

    eprintln!("âœ… PTY Controller: Test passed!");
}

/// PTY Controlled: Read and parse mouse events
fn pty_controlled_entry_point() -> ! {
    println!("{CONTROLLED_READY}");
    std::io::stdout().flush().expect("Failed to flush");

    let runtime = tokio::runtime::Runtime::new().expect("Failed to create Tokio runtime");

    runtime.block_on(async {
        eprintln!("ğŸ” PTY Controlled: Starting...");
        let mut input_device = DirectToAnsiInputDevice::new();
        eprintln!("ğŸ” PTY Controlled: Device created, reading events...");

        let inactivity_timeout = Duration::from_secs(2);
        let mut inactivity_deadline = tokio::time::Instant::now() + inactivity_timeout;
        let mut event_count = 0;

        loop {
            tokio::select! {
                event_result = input_device.next() => {
                    match event_result {
                        Some(event) => {
                            event_count += 1;
                            inactivity_deadline = tokio::time::Instant::now() + inactivity_timeout;
                            eprintln!("ğŸ” PTY Controlled: Event #{event_count}: {event:?}");

                            let output = match event {
                                InputEvent::Mouse(ref mouse_input) => {
                                    format!("Mouse: {mouse_input:?}")
                                }
                                _ => {
                                    format!("Event: {event:?}")
                                }
                            };

                            println!("{output}");
                            std::io::stdout().flush().expect("Failed to flush stdout");

                            if event_count >= 6 {
                                eprintln!("ğŸ” PTY Controlled: Processed {event_count} events, exiting");
                                break;
                            }
                        }
                        None => {
                            eprintln!("ğŸ” PTY Controlled: EOF reached");
                            break;
                        }
                    }
                }
                () = tokio::time::sleep_until(inactivity_deadline) => {
                    eprintln!("ğŸ” PTY Controlled: Inactivity timeout (2 seconds with no events), exiting");
                    break;
                }
            }
        }

        eprintln!("ğŸ” PTY Controlled: Completed, exiting");
    });

    eprintln!("ğŸ” Controlled: Completed, exiting");
    std::process::exit(0);
}
